mapper.insert:
insert into tasks(task_type,region_id,image_id,
architecture,
os_type,
language,platform,is_install_windows_update,
system_disk_format,
system_disk_size,oss_bucket, storage_url,user_defined_script_url,script_Related_file_url, app_key,app_secret,sign,request_id,client_token,version,storage_checksum,task_id,result_image_oss_bucket,result_image_oss_object,start_time, finished_time,storage_type,is_kvm_enabled,is_xen_enabled,gmt_create,gmt_modify
,status
) VALUES (#{taskType},#{regionId},#{imageId},
#{architecture},
#{osType},
#{language},#{platform},#{isInstallWindowsUpdate},
#{systemDiskFormat},
#{systemDiskSize},#{ossBucket}, #{storageUrl},#{userDefinedScriptUrl},#{scriptRelatedFileUrl}, #{appKey},#{appSecret},#{sign},#{requestId},#{clientToken},#{version}, #{storageChecksum},#{taskId},#{resultImageOssBucket},#{resultImageOssObject},#{startTime},#{finishedTime}, #{storageType},#{isKvmEnabled},#{isXenEnabled},#{gmtCreate},#{gmtModify}
,#{status}
);

mapper.select:
select t.region_id,t.task_id,t.task_type,t.status,t.gmt_create,
t.progress,t.expected_time,t.is_support_cancel,t.finished_time,
cr.item_name,cr.item_desc,cr.item_label,cr.ok,cr.info,cr.is_fix_enabled,cr.is_fix_ok,cr.fix_info from tasks t left
join check_results cr on t.task_id = cr.task_id
where t.region_id = #{regionId} and t.task_id in

    #{item}


    and start_time >= #{startTime}


    and finished_time <= #{endTime}


    and status = #{taskStatus}

order by t.gmt_create DESC limit #{offset},#{pageSize}

select count(*) from tasks where region_id = #{regionId} and task_id in

    #{item}


    and start_time >= #{startTime}


    and finished_time <= #{endTime}


    and status = #{taskStatus}
